import React, {FC, useEffect, useState} from "react"
import { Formik, Field, Form, FormikHelpers } from 'formik';

import { Params } from '../../App';
import {makeUnique} from "../../helpers/makeUnique";
import {capitalize} from "../../helpers/capitalize";
import Title from "../Title/Title";
import {Settlement} from "../../types";
import {findYaCodes} from "../../helpers/findYaCodes";

export interface Values {
  from: string;
  to: string;
  line: string;
  // date?: Date | null;
}

interface Props {
  setParams: React.Dispatch<React.SetStateAction<Values>>;
  stations: any[];
  setYaCodeTo: React.Dispatch<React.SetStateAction<string>>;
  setYaCodeFrom: React.Dispatch<React.SetStateAction<string>>;
}

const Finder: FC<Props> = ({setParams, stations, setYaCodeTo, setYaCodeFrom}) => {
  const [lines, setLines] = useState([]);

  useEffect(() => {
    setLines(
      stations
        .filter((obj: any) => obj.title === 'Москва')[0].stations.filter((obj: any) => obj.direction)
        .map((obj: any) => obj.direction).filter(makeUnique)
    )
  }, [stations])


  return (
    <>
      <Formik
        initialValues={{
          from: '',
          to: '',
          line: '',
          // date: null,
        }}
        onSubmit={(
          values: Values,
          { setSubmitting }: FormikHelpers<Values>
        ) => {
          setParams({...values, from: capitalize(values.from), to: capitalize(values.to)});

//          console.log({...values, from: capitalize(values.from), to: capitalize(values.to)});
          const {fromYaCode, toYaCode} = findYaCodes(values.from, values.to, stations);
          setYaCodeFrom(fromYaCode);
          setYaCodeTo(toYaCode);
          setSubmitting(false);
        }}
      >
        <Form>
          <label htmlFor="from">Откуда</label>
          <Field id="from" name="from" placeholder="Гражданская" />

          <label htmlFor="to">Куда</label>
          <Field id="to" name="to" placeholder="Нахабино" />

          <label htmlFor="line">Направление</label>
            <Field
              component="select"
              id="line"
              name="line"
              multiple={false}>
          {lines.map((line) => <option value={line} key={line}>{line}</option>)}
            </Field>

          <button type="submit">Submit</button>
        </Form>
      </Formik>
    </>
  )
}

export default Finder
