import React, {FC, memo} from 'react';

import classes from './Flight.module.scss';
import moment from "moment";
import {convertSeconds} from "../../helpers/convertSeconds";

interface Props {
	key: string;
	index: number;
	platform: string;
	stops: string;
	arrival: string | Date | null;
	departure: string | Date | null;
	duration: number;
	title: string; // ""Новоиерусалимская — Серпухов""
	subtype: string; // Стандарт плюс
	price: number | any;

}

const Flight: FC<Props> = memo((
	{
		key,
		index,
		platform,
		stops,
		arrival,
		departure,
		duration,
		title,
		subtype,
		price,
	}) => {
	return (
		<div className={classes.wrapper} key={key}>
			<div className={classes.segment}>
				<div>#</div>
				<div>{index + 1}</div>
			</div>
			<div className={classes.segment}>
				<div>Эррайвл</div>
				<div>moment</div>
			</div>
			<div className={classes.segment}>
				<div>Депарча</div>
				<div>moment</div>
			</div>
			<div className={classes.segment}>
				<div>продолжительность остановки</div>
				<div>посчитать</div>
			</div>
			<div className={classes.segment}>
				<div>Время в пути</div>
				<div>{convertSeconds(duration)}</div>
			</div>
			<div className={classes.segment}>
				<div>Стоимость</div>
				<div>{price ? price + 'р.' : '??'}</div>
			</div>
			<div className={classes.segment}>
				<div>
					Путь
				</div>
				<div>
					{platform}
				</div>
			</div>
			<div className={classes.segment}>
				<div>Фулл маршрут</div>
				<div>{title}</div>
			</div>
		</div>
	);
});

export default Flight;
