import React, {FC, memo} from "react"
import Loader from "../../components/Loader/Loader";

import classes from './Schedule.module.scss';
import {Segment} from "../../types";
import Flight from "../../components/Flight/Flight";
//   https://api.rasp.yandex.net/v3.0/search/?apikey=b4bb1709-f368-422c-9466-87821be7f87c&format=json&from=s9600871&to=s9602460&lang=ru_RU&page=1&date=2022-09-04

const Schedule: FC<{schedule: any}> = memo(({schedule}) => {
  if (schedule === undefined || Object.values(schedule).length === 0) {
    return <Loader />;
  }

console.log(schedule)
  const {date, from, to} = schedule.search;

  const {segments} = schedule;
  //???? const options: {year: string, month: string, day: string} = { year: 'numeric', month: 'long', day: 'numeric' }

  return (
    <>
      <div className={classes.fromTo}>
        <div>Откуда: {from.title}</div>
        <div>Куда: {to.title}</div>
        <div>Когда: {new Date(date).toLocaleDateString('ru-RU', { year: 'numeric', month: 'long', day: 'numeric' })}</div>
      </div>
      {segments.map((segment: Segment, i: number) => {
        return (
          <Flight
            key={segment.thread.uid}
            index={i}
            platform={segment.arrival_platform}
            stops={segment.stops}

          />
        )
      })}
    </>
  )
})

export default Schedule
