import { observer } from 'mobx-react';
import {toJS} from 'mobx';

import Finder from './features/Finder/Finder';
import {Values} from './features/Finder/Finder';
import './App.css';
import Schedule from './features/Schedule/Schedule';
import ScheduleStore from './features/Schedule/ScheduleStore';
import {key} from './constants/key';
// import {bigData} from './features/bigData';
// import {Simulate} from "react-dom/test-utils";
import axios from "axios";
import {thnxToYandex} from "./constants/thnxToYandex";
import {makeUnique} from "./helpers/makeUnique";
import Loader from "./components/Loader/Loader";
import Title from "./features/Title/Title";
import {Settlement} from "./types";
import {findYaCodes} from "./helpers/findYaCodes";
import {findYaCode} from "./helpers/findYaCode";
import moment from "moment";
import {today, tomorrow} from "./constants/dates";

export interface Params {
  from: string;
  to: string;
}

// const url = '/v3.0/stations_list/';


function App() {
  const [params, setParams] = useState<Params>({from: "", to: ""})

  const [allStations, setAllStations] = useState<Settlement[]>([]);

  const [schedule, setSchedule] = useState({});

  const [tomorrow, setTomorrow] = useState(false);
  const [currentDate, setCurrentDate] = useState<string>(today); // todo type?


  // const [yaCodeFrom, setYaCodeFrom] = useState<string>('');
  // const [yaCodeTo, setYaCodeTo] = useState<string>('');
  // const [finder, setFinder] = useState(false);
  // const [regions, setRegions] = useState([]);
  // const [dogs, setDogs] = useState([]);
  // const { getSchedules, schedules } = ScheduleStore;




  // from to
  // https://api.rasp.yandex.net/v3.0/search/?apikey=b4bb1709-f368-422c-9466-87821be7f87c&format=json&from=s9600871&to=s9602460&lang=ru_RU&page=1&date=2022-09-04
  // schedules
  // https://api.rasp.yandex.net/v3.0/stations_list/?direction=на%20Москву&lang=ru_RU&format=json&apikey=b4bb1709-f368-422c-9466-87821be7f87c

  // https://api.rasp.yandex.net/v3.0/search/?apikey=b4bb1709-f368-422c-9466-87821be7f87c&format=json&from=s9600871&to=s9602460&lang=ru_RU&page=1&date=2022-09-04

  // на выходе должен получиться массив с объектами такого типа
  // codes: Object { esr_code: "191602", yandex_code: "s2000001" };
  // direction: "Горьковское";
  // latitude: 55.757383;
  // longitude: 37.660784;
  // station_type: "train_station";
  // title: "Москва (Курский вокзал)";
  // transport_type: "train";


//   useEffect(() => {
//     axios.get(`/v3.0/stations_list/?direction=на%20Москву&lang=ru_RU&apikey=b4bb1709-f368-422c-9466-87821be7f87c
// &transport_types=suburban`)
//       .then((res) => console.log(res.data.countries // console.log --  setAllStations
//           .find((obj: any) => obj.title === 'Россия').regions
//           .find((obj: any) => obj.title === 'Москва и Московская область').settlements
//           .map((obj) => obj.title).sort() // 340 нет
//         // .find((obj: any) => obj.direction).codes.yandex_code
//       ));
//   }, []);

  useEffect(() => {
    axios.get(`/v3.0/stations_list/?direction=на%20Москву&lang=ru_RU&format=json&apikey=${key}`)
      .then((res) => setAllStations(res.data.countries // console.log --  setAllStations
        .find((obj: any) => obj.title === 'Россия').regions
        .find((obj: any) => obj.title === 'Москва и Московская область').settlements

        // .find((obj: any) => obj.title === 'Нахабино').stations
        // .find((obj: any) => obj.direction).codes.yandex_code
      ));
  }, []);


  useEffect(() => {
    const {from, to} = params;

    if (from !== undefined && to !== undefined && from.length > 0 && to.length > 0) {
      axios.get(`/v3.0/search/?apikey=${key}&format=json&from=${from}&to=${to}&lang=ru_RU&page=1&date=${currentDate}`)
        .then((res) => setSchedule(res.data))
    }
  }, [params, currentDate])

  // todo прочекать функцию

  //   axios
  //     .all([
  //       axios.get(`/v3.0/copyright/?apikey=b4bb1709-f368-422c-9466-87821be7f87c&format=json`),
  //       axios.get(`/v3.0/stations_list/?direction=на%20Москву&lang=ru_RU&format=json&apikey=b4bb1709-f368-422c-9466-87821be7f87c`)])
  //     .then(
  //       axios.spread((el1, el2) => {
  //         const url = el1.data;
  //         setYaStr(url);
  //
  //         const dogs = el2.data;
  //         setDogs(dogs);
  //       })
  //     );
  // }, []);

  if (!allStations || allStations.length === 0) {
    return <Loader />
  }

  return (
    <div className="App">
      <Title tomorrow={tomorrow} setTomorrow={setTomorrow} currentDate={currentDate} setCurrentDate={setCurrentDate}/>
      <Finder
        setParams={setParams}
        stations={allStations}
      />
      <Schedule schedule={schedule} />
      <footer><p>Данные предоставлены сервисом <span className="ya">Яндекс.Расписания</span></p></footer>
    </div>
  );
}

export default observer(App);
