import moment from "moment";

import {Segment} from "../types";

export const separatePastAndFuture = (arr: Segment[]) => {
	const now = moment();
	const past = [];
	const future = [];

	for (let i = 0; i <= arr.length; i++) {
		if (now > moment(new Date(arr[i].departure))) {
			past.push(arr[i])
		} else if (now < moment(new Date(arr[i].departure))) {
			future.push(arr[i])
		}
	}

	return {past, future};
}
