import React, {DetailedHTMLProps, FC, FormEventHandler, FormHTMLAttributes, useState} from 'react';

const SimpleFinder: FC<any> = ({from, setFrom, to, setTo}) => {
	// const [from, setFrom] = useState('');
	// const [to, setTo] = useState('');

	const [swap, setSwap] = useState(false);

	const handleSubmit = (e: any) => {
		e.preventDefault();
		if (swap) {
			console.log({from: to, to: from})
		}
		if (!swap) {
			console.log({from, to})
		}
	}

	console.log('simple dimple')
	return (
		<form onSubmit={handleSubmit}>
			<input
				type="text"
				name={'from'}
				value={swap ? to : from}
				onChange={e => setFrom(e.target.value)}
				placeholder="Откуда"
			/>
			<button
				type="button"
				onClick={() => setSwap(!swap)}
				style={{fontSize: '16px', fontWeight: 700}}
			>&#x21C6;</button>
			<input
				type="text"
				name={'to'}
				value={swap ? from : to}
				onChange={e => setTo(e.target.value)}
				placeholder="Куда" />

			<button type="submit">Submit</button>
		</form>
	);
};

export default SimpleFinder;
