import React, { useEffect, useState } from 'react';
import { observer } from 'mobx-react';

import Finder from './features/Finder/Finder';
import './App.css';
import Schedule from './features/Schedule/Schedule';
import {key} from './constants/key';
import axios from "axios";
import {thnxToYandex} from "./constants/thnxToYandex";
import Loader from "./components/Loader/Loader";
import Title from "./features/Title/Title";
import {Settlement} from "./types";
import {today, tomorrow} from "./constants/dates";

export interface Params {
  from: string;
  to: string;
}

// const url = '/v3.0/stations_list/';


function App() {
  const [params, setParams] = useState<Params>({from: "", to: ""})

  const [allStations, setAllStations] = useState<Settlement[]>([]);

  const [schedule, setSchedule] = useState({});

  const [tomorrow, setTomorrow] = useState(false);
  const [currentDate, setCurrentDate] = useState<string>(today); // todo type?

  const [defaultFinder, setDefaultFinder] = useState(true);

  // from to
  // https://api.rasp.yandex.net/v3.0/search/?apikey=b4bb1709-f368-422c-9466-87821be7f87c&format=json&from=s9600871&to=s9602460&lang=ru_RU&page=1&date=2022-09-04
  // schedules
  // https://api.rasp.yandex.net/v3.0/stations_list/?direction=на%20Москву&lang=ru_RU&format=json&apikey=b4bb1709-f368-422c-9466-87821be7f87c

  // https://api.rasp.yandex.net/v3.0/search/?apikey=b4bb1709-f368-422c-9466-87821be7f87c&format=json&from=s9600871&to=s9602460&lang=ru_RU&page=1&date=2022-09-04


  // old first request
  // useEffect(() => {
  //   axios.get(`v3.0/stations_list/?direction=на%20Москву&lang=ru_RU&apikey=${key}&transport_types=suburban`)
  //     .then((res) => setAllStations(res.data.countries
  //       .find((obj: any) => obj.title === 'Россия').regions
  //       .find((obj: any) => obj.title === 'Москва и Московская область').settlements
  //     ));
  // }, []);

  useEffect(() => {
    axios.get(`http://127.0.0.1:3001/`)
      .then((res) => setAllStations(res.data))
      .catch(e => console.error(e))
  }, []);


  useEffect(() => {
    const {from, to} = params;

    if (from !== undefined && to !== undefined && from.length > 0 && to.length > 0) {
      axios.get(`/v3.0/search/?apikey=${key}&format=json&from=${from}&to=${to}&lang=ru_RU&page=1&date=${currentDate}`)
        .then((res) => setSchedule(res.data))
    }
  }, [params, currentDate])

  if (!allStations || allStations.length === 0) {
    return <Loader />
  }

  return (
    <div className="App">
      <Title tomorrow={tomorrow} setTomorrow={setTomorrow} currentDate={currentDate} setCurrentDate={setCurrentDate}/>
      <button type="button" onClick={() => setDefaultFinder(!defaultFinder)}>{defaultFinder ? 'дефолт' : 'экзотик'}</button>
      {defaultFinder && <Finder setParams={setParams} stations={allStations} />}
      {!defaultFinder && <>exotic finder</>}
      <Schedule schedule={schedule} currentDate={currentDate} />
      <footer
        style={{
          position: 'fixed',
          left: 0,
          bottom: 0,
          width: '100%',
          textAlign: 'center',
        }}
      >
        <p style={{fontSize: '5px'}}>{thnxToYandex}</p>
      </footer>
    </div>
  );
}

export default observer(App);
