import React, {FC, useCallback, useEffect, useState} from "react"
import { Formik, Field, Form, FormikHelpers } from 'formik';

import {capitalize} from "../../helpers/capitalize";
import {findYaCode} from "../../helpers/findYaCode";
import {values} from "mobx";

export interface Values {
  from: string;
  to: string;
  // line: string;
  // date?: Date | null;
}

interface Props {
  setParams: React.Dispatch<React.SetStateAction<Values>>;
  stations: any[];
}

// ...props ?
// todo disabled swap btn
const Finder: FC<Props> = ({setParams, stations}) => {
  const [swap, setSwap] = useState(false);



  const handleSubmit = useCallback(() => ( values: Values, { setSubmitting }: FormikHelpers<Values>) => {
    if (!swap) {
      setParams({
        from: findYaCode(capitalize(values.from), stations),
        to: findYaCode(capitalize(values.to), stations)});
    }
    if (swap) {
      setParams({
        from: findYaCode(capitalize(values.to), stations),
        to: findYaCode(capitalize(values.from), stations)
      });
    }
    setSubmitting(false);
  }, [])

  useEffect(() => {
    handleSubmit();
  }, [swap, handleSubmit])

  return (
    <>
      <Formik
        initialValues={{
          from: '',
          to: '',
        }}
        onSubmit={handleSubmit}
      >
        <Form>
          <Field id={!swap ? 'from' : 'to'} name={!swap ? 'from' : 'to'} placeholder="Откуда" />

          <button
            type="button"
            onClick={() => setSwap(!swap)}
            style={{fontSize: '16px', fontWeight: 700}}
          >&#x21C6;</button>

          <Field id={swap ? 'from' : 'to'} name={swap ? 'from' : 'to'} placeholder="Куда" />

          <button type="submit">Submit</button>
        </Form>
      </Formik>
    </>
  )
}

export default Finder
