import React, {FC, memo} from 'react';

import classes from './Flight.module.scss';
import moment from "moment";
import {convertSeconds} from "../../helpers/convertSeconds";
import {convertDateToHHmm} from "../../helpers/convertDateToHHmm";

interface Props {
	key: string;
	index: number;
	platform: string;
	stops: string;
	arrival: Date;
	departure: Date;
	duration: number;
	title: string; // ""Новоиерусалимская — Серпухов""
	subtype: string; // Стандарт плюс
	price: number | any;

}

const Flight: FC<Props> = memo((
	{
		key,
		index,
		platform,
		stops,
		arrival,
		departure,
		duration,
		title,
		subtype,
		price,
	}) => {
	return (
		<div className={classes.wrapper} key={key}>
			<div className={classes.segment}>
				<div>{index + 1}</div>
			</div>
			<div className={classes.segment}>
				<div>{convertDateToHHmm(arrival)}</div>
			</div>
			<div className={classes.segment}>
				<div>{convertDateToHHmm(departure)}</div>
			</div>
			<div className={classes.segment}>
				<div>продолжительность остановки</div>
				<div>посчитать</div>
			</div>
			<div className={classes.segment}>
				<div>Время в пути</div>
				<div>{convertSeconds(duration) + ' мин.'}</div>
			</div>
			<div className={classes.segment}>
				<div>Стоимость</div>
				<div>{price ? price + 'р.' : '??'}</div>
			</div>
			<div className={classes.segment}>
				<div>
					Путь
				</div>
				<div>
					{platform ?? 'самурая'}
				</div>
			</div>
			<div className={classes.segment}>
				<div>{title}</div>
			</div>
		</div>
	);
});

export default Flight;
