import React, {FC, useEffect, useState} from "react"
import { Formik, Field, Form, FormikHelpers } from 'formik';

import { Params } from '../../App';
import {makeUnique} from "../../helpers/makeUnique";

export interface Values {
  from: string;
  to: string;
  direction: string;
  // date?: Date | null;
}

interface Props {
  setParams: React.Dispatch<React.SetStateAction<Values>>;
  stations: any[];
}

const Finder: FC<Props> = ({setParams, stations}) => {
  const [lines, setLines] = useState([]);

  useEffect(() => {
    setLines(
      stations
        .filter((obj: any) => obj.title === 'Москва')[0].stations.filter((obj: any) => obj.direction)
        .map((obj: any) => obj.direction).filter(makeUnique)
    )
  }, [stations])


  return (
    <div>
      <h1>Signup</h1>
      <Formik
        initialValues={{
          from: '',
          to: '',
          direction: '',
          // date: null,
        }}
        onSubmit={(
          values: Values,
          { setSubmitting }: FormikHelpers<Values>
        ) => {
          setParams(values);
          setSubmitting(false);
        }}>
        <Form>
          <label htmlFor="from">Откуда</label>
          <Field id="from" name="from" placeholder="Гражданская" />

          <label htmlFor="to">Куда</label>
          <Field id="to" name="to" placeholder="Нахабино" />

          <label htmlFor="direction">Направление</label>
          <Field
            id="direction"
            name="direction"
            placeholder="Рижское"
          />

          {/* <label htmlFor="date">Дата</label>
          <Field
            id="date"
            name="date"
            placeholder="Рижское"
            type="date"
          /> */}


          <label htmlFor="location">Line</label>
            <Field
              component="select"
              id="location"
              name="location"
              multiple={false}>
          {lines.map((line) => <option value={line} key={line}>{line}</option>)}
            </Field>

          <button type="submit">Submit</button>
        </Form>
      </Formik>
    </div>
  )
}

export default Finder
