import React, {FC, useState} from "react"
import { Formik, Field, Form, FormikHelpers } from 'formik';

import classes from './Finder.module.scss';

export interface Values {
  from: string;
  to: string;
}

interface Props {
  setParams?: React.Dispatch<React.SetStateAction<Values>>;
}

const Finder: FC<Props> = ({setParams, }) => {
  const [swap, setSwap] = useState(false);

 // console.log('finder');

  return (
    <>
      <Formik
        initialValues={{
          from: '',
          to: '',
        }}
        onSubmit={( values: Values, { setSubmitting }: FormikHelpers<Values>) => {
          // if (!swap) {
          //   setParams({
          //     from: findYaCode(capitalize(values.from), stations),
          //     to: findYaCode(capitalize(values.to), stations)});
          // }
          // if (swap) {
          //   setParams({
          //     from: findYaCode(capitalize(values.to), stations),
          //     to: findYaCode(capitalize(values.from), stations)
          //   });
          // }
          if (!swap && setParams) {
            setParams({
              from: values.from,
              to: values.to
            });
          }
          if (swap && setParams) {
            setParams({
              from: values.to,
              to: values.from
            });
          }
          setSubmitting(false);
        }}
      >
        <Form>
          <Field id={!swap ? 'from' : 'to'} name={!swap ? 'from' : 'to'} placeholder="Откуда" />

          <button
            type="button"
            onClick={() => setSwap(!swap)}
            className={classes.swap}
            data-testid="swapper"
          >⇄</button>

          <Field id={swap ? 'from' : 'to'} name={swap ? 'from' : 'to'} placeholder="Куда" />

          <button type="submit" data-testid="submit">Submit</button>
        </Form>
      </Formik>
    </>
  )
}

export default Finder
