import React, {FC, memo, useState} from "react"
import Loader from "../../components/Loader/Loader";

import {Segment, Status} from "../../types";
import Flight from "../../components/Flight/Flight";
import {separatePastAndFuture} from "../../helpers/separatePastAndFuture";
import moment from "moment";
import {today} from "../../constants/dates";
import Steps from "../../components/Steps/Steps";
import Dog from "../../components/Dog/Dog";
//   https://api.rasp.yandex.net/v3.0/search/?apikey=b4bb1709-f368-422c-9466-87821be7f87c&format=json&from=s9600871&to=s9602460&lang=ru_RU&page=1&date=2022-09-04

interface Props {
  schedule: any;
  currentDate: string;
  // scheduleStatus: Status;
}

const Schedule: FC<Props> = memo(({schedule, currentDate,}) => {
  // const [pastAndFuture, setPastAndFuture] = useState<{past: Segment[], future: Segment[]}>({past: [], future: []})
  const [showPast, setShowPast] = useState(false);


  // if (schedule === undefined || Object.values(schedule).length === 0) {
  //   return <Dog/>;
  // }

  // if (scheduleStatus === 'loading') {
  //   return <Dog />;
  // }

// todo почитать что не может быть стейтом))


  // console.log({schedule})
  const {date, from, to} = schedule.search;


  const {segments} = schedule;
  const {past, future} = separatePastAndFuture(segments);
  //???? const options: {year: string, month: string, day: string} = { year: 'numeric', month: 'long', day: 'numeric' }


  return (
    <>
      {/*<div className={classes.fromTo}>*/}
        <div>Откуда: {from.title}</div>
        <div>Куда: {to.title}</div>
        <div>Когда: {new Date(date).toLocaleDateString('ru-RU', { year: 'numeric', month: 'long', day: 'numeric' })}</div>
      {/*</div>*/}

      {showPast && past.map((segment: Segment, i: number) => {
        return (
          <Flight
            key={i + ""}
            stops={segment.stops}
            arrival={segment.arrival}
            departure={segment.departure}
            duration={segment.duration}
            title={segment.thread.short_title}
            subtype={segment.thread.transport_subtype.title}
            price={segment.tickets_info?.places[0].price.whole}
          />
        )
      })}
      {currentDate === today && <button type="button"
               onClick={() => setShowPast(!showPast)}>{!showPast ? 'Показать ушедние собаки' : 'Скрыть ушедшие собаки'}</button>}
      {future.map((segment: Segment, i: number) => {
        return (
          <Flight
            key={i + ""}
            stops={segment.stops}
            arrival={segment.arrival}
            departure={segment.departure}
            duration={segment.duration}
            title={segment.thread.short_title}
            subtype={segment.thread.transport_subtype.title}
            price={segment.tickets_info?.places[0].price.whole}
          />
        )
      })}
    </>
  )
})

export default Schedule
